import { FC } from "react";
import { CourseTagProps } from "./props";
import styled, { css } from "styled-components";

const TagComponent = styled.div<{ $primary?: boolean }>`
  width: -webkit-fill-available;
  color: #39414b;
  height: 42px;
  border-radius: 12px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  padding-left: 12px;
  padding-right: 12px;
  cursor: pointer;

  font-family: "Nunito";
  font-style: normal;
  font-weight: 800;
  font-size: 18px;
  line-height: 18px;
  letter-spacing: -0.1px;

  &:hover {
    background: #5fbf77;
    color: #ffffff;
  }

  ${(props) =>
    props.$isActive &&
    css`
      background: #5fbf77;
      color: #ffffff;
    `};
`;

export const CourseTag: FC<CourseTagProps> = ({ name, isActive }) => {
  return <TagComponent>{name}</TagComponent>;
};
