import { FC } from "react";
import { CoursesTagsSideBarProps } from "./props";
import styled from "styled-components";
import { CourseTag } from "../../atoms/courseTag";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px;
  width: 264px;
  border: 1px solid #cfcfe4;
  border-radius: 18px;
`;

export const CoursesTagsSideBar: FC<CoursesTagsSideBarProps> = ({
  tags,
  currentTag,
}) => {
  return (
    <Container>
      <CourseTag name={"Все темы"} isActive={!currentTag} />
      {tags.map((tag) => (
        <CourseTag name={tag} isActive={false} />
      ))}
    </Container>
  );
};
