import { useContext, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import { UserContext } from "@context/UserContext";

import { FullpageSpinner } from "@components/atoms/FullpageSpinner";
import { AlertComponent } from "@components/atoms/AlertComponent";
import { UserLayout } from "@components/templates/UserLayout";

import { ServicesListPage } from "@/pages/ServicesListPage";
import { VacationApplicationPage } from "@/pages/VacationApplicationPage";
import { VacationBalancePage } from "@/pages/VacationBalancePage";
import { PayslipPage } from "@/pages/PayslipPage";
import { BusinessTripApplicationPage } from "@/pages/BusinessTripApplicationPage";
import { SocialVacationApplicationPage } from "@/pages/SocialVacationApplicationPage";
import { RefundApplicationPage } from "@/pages/RefundApplicationPage";
import { EmploymentCertificatePage } from "@/pages/EmploymentCertificatePage";
import { ProofOfEmployment } from "@/pages/ProofOfEmployment";
import { CabinetRoutes } from "@modules/cabinet/CabinetRoutes";

function AppRoutes() {
  const { isSettled, authToken, loadUserInfo } = useContext(UserContext);

  useEffect(() => {
    loadUserInfo();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [authToken]);

  if (isSettled) {
    return (
      <FullpageSpinner
        title="Проверка авторизационного ключа"
        subtitle="При проблемах проверьте своё подключение к интернету или к VPN"
      />
    );
  }

  return (
    <Routes>
      <Route element={<UserLayout />}>
        <Route path="/" element={<ServicesListPage />} />
        <Route
          path="/error"
          element={
            <AlertComponent
              message="В данный момент сервис не доступен"
              type="error"
              closable={false}
            />
          }
        />
      </Route>

      <Route element={authToken ? <UserLayout /> : <Navigate to={"/"} />}>
        <Route path="/cabinet/*" element={<CabinetRoutes />} />

        <Route path="/payslip" element={<PayslipPage />} />
        <Route path="/vacation-balance" element={<VacationBalancePage />} />
        <Route
          path="/vacation-application"
          element={<VacationApplicationPage />}
        />

        <Route
          path="/employment-certificate"
          element={<EmploymentCertificatePage />}
        />
        <Route path="/refund-application" element={<RefundApplicationPage />} />
        <Route
          path="/social-vacation-application"
          element={<SocialVacationApplicationPage />}
        />
        <Route
          path="/business-trip-application"
          element={<BusinessTripApplicationPage />}
        />
        <Route path="/proof-of-employment" element={<ProofOfEmployment />} />
      </Route>
    </Routes>
  );
}

export default AppRoutes;
